mapper.select:
SELECT f.FileID, f.FileName, f.Summary, f.AppliCom, f.AppliComCode, f.UploadDate, f.UploadPerson, if(f.fileNameCode='',f.fileNameCode,concat(#{url},f.fileNameCode)) as fileNameCode FROM file AS f
and fileName like '%${fileName}%'
and UploadDate between #{startDate} and #{endDate}
and substring(appliComCode,1,length(#{appliComCode}))=#{appliComCode}
and uploadPersonId = #{uploadPersonId}
and fileType = #{fileType}
order by f.UploadDate desc

SELECT count(*)
FROM
file AS f


        and fileName like '%${fileName}%'


        and UploadDate between #{startDate} and #{endDate}


        and substring(appliComCode,1,length(#{appliComCode}))=#{appliComCode}


        and uploadPerson like '%${uploadPerson}%'


        and fileType =#{fileType}
